import React from "react";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import "./imageSlider.css";
import $ from "jquery";

const ImageSlider = () => {
  $(document).ready(function () {
    var height = $('.image').height();
    $('.image-container').css('height', 'calc(' + height + 'px' + ' - 18vh');
    $(window).resize(function () {
      var height = $('.anime-info').height();
      $('.black-div').css('height', 'calc(' + height + 'px' + ' - 18vh');
    });
  });
  let settings = {
    dots: false,
    infinite: true,
    speed: 500,
    slideToShow: 1,
    slideToScroll: 1,
    autoplay: true,
    arrows: false,
  };
  return (
    <div className="bg-white slider">
      <Slider {...settings} className="container slider mx-auto">
        <div className="image-container">
          <div className="image">
            <a href="/">
              <img src="/assets/images/hxh.webp" alt="" />
            </a>
          </div>
        </div>
        <div className="image-container">
          <div className="image">
            <a href="/">
              <img src="/assets/images/hxh.webp" alt="" />
            </a>
          </div>
        </div>
        <div className="image-container">
          <div className="image">
            <a href="/">
              <img src="/assets/images/hxh.webp" alt="" />
            </a>
          </div>
        </div>
      </Slider>
    </div>
  );
};

export default ImageSlider;
