import React from "react";

const Comments = () => {
  return (
    <div className="comments-container bg-gray-400">
      <div className="comment-header">comment</div>
      <div className="comment-content grrid grid-cols-1 sm:grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-3 gap-4 bg-red-500">
        <div>
          <div className="comment-writer flex">
            <div className="comment-writer-img">img</div>
            <div className="comment-wirter-info">
              <div className="comment-writer-name">name</div>
              <div className="comment-time">time</div>
            </div>
          </div>
          <div className="comment-text">text</div>
          <div className="comment-anime">comment anime</div>
        </div>
      </div>
    </div>
  );
};

export default Comments;
