import React, { useRef, useEffect } from "react";

import tinySlider from "tiny-slider-react";
import AnimeSidebar from "../components/sidebars/AnimeSidebar";
import Navbar from "../components/navbar/Navbar";

const AnimeHome = () => {
  const sliderRef = useRef(null);

  useEffect(() => {
    const slider = tinySlider({
      container: sliderRef.current,
      items: 1,
      slideBy: "page",
      autoplay: true,
      controls: false,
    });
  }, []);
  return (
    <div>
      <Navbar></Navbar>
      <AnimeSidebar></AnimeSidebar>
      <div ref={sliderRef} className="image-slider">
        <div className="slide">
          <img src="/assets/images/me- AI.png" alt="Image 1" />
          <p>Texte pour l'image 1</p>
        </div>
        <div className="slide">
          <img src="/assets/images/me- AI.png" alt="Image 2" />
          <p>Texte pour l'image 2</p>
        </div>
        <div className="slide">
          <img src="image3.jpg" alt="Image 3" />
          <p>Texte pour l'image 3</p>
        </div>
      </div>
    </div>
  );
};

export default AnimeHome;
