import React from "react";
import { useEffect, useRef } from "react";
import VanillaTilt from "vanilla-tilt";

import "./comments.css";

function Tilt(props) {
  const { options, ...rest } = props;
  const tilt = useRef(null);

  useEffect(() => {
    VanillaTilt.init(tilt.current, options);
  }, [options]);

  return <div ref={tilt} {...rest} />;
}

const Comments = () => {
  const options = {
    speed: 500,
    max: 20,
  };
  return (
    <div className="comments-container bg-slate-700 p-4 mb-6 rounded-lg">
      <div className="comment-header flex mb-4">
        <div className="newest-comments border-solid border border-pink-600 py-1.5 px-3 text-md font-medium text-pink-600 rounded-lg hover:bg-white hover:text-pink-600 cursor-pointer mr-6">
          Newest Comments
        </div>
        <div className="newest-comments border-solid border border-white py-1.5 px-3 text-md font-medium text-white rounded-lg hover:bg-pink-600 hover:text-white cursor-pointer">
          Top Comments
        </div>
      </div>
      <div class="h-64 flex flex-col">
        <div class="bg-gray-300 h-16 flex items-center justify-center">
          Header
        </div>
        <div class="comment-text">
          <div class="">Contenu qui peut d√©passer 3 lignes Lorem ipsum dolor sit amet consectetur adipisicing elit. Error ducimus odio ex perspiciatis ea provident exercitationem a dolore, explicabo dolor est, facilis nam voluptatem! Eaque modi enim illo ducimus, error repellendus fugiat nam quisquam veniam magni ullam doloribus velit dolores voluptatem laborum odio sequi iste repellat incidunt vitae quas mollitia.</div>
        </div>
        <div class="bg-gray-300 h-8 flex items-center justify-center">
          Badge
        </div>
      </div>
    </div>
  );
};

export default Comments;
