import React from "react";
import { useEffect, useRef, useState } from "react";
import VanillaTilt from "vanilla-tilt";

import "./trending.css";

import OwlCarousel from "react-owl-carousel";
import "owl.carousel/dist/assets/owl.carousel.css";
import "owl.carousel/dist/assets/owl.theme.default.css";

function Tilt(props) {
  const { options, ...rest } = props;
  const tilt = useRef(null);

  useEffect(() => {
    VanillaTilt.init(tilt.current, options);
  }, [options]);

  return <div ref={tilt} {...rest} />;
}


const Trending = () => {
  const carouselRef = React.useRef();

  const handleNextClick = () => {
    carouselRef.current.next();
  };

  const handlePrevClick = () => {
    carouselRef.current.prev();
  };
  const [itemsToShow, setItemsToShow] = useState(6);

  useEffect(() => {
    if (performance.navigation.type === 1) {
      // If the page has been reloaded
      setItemsToShow(5); // show 6 items on reload
    }
  }, []);

  const swiper_options = {
    items: { itemsToShow },
    loop: false,
    responsiveClass: true,
    responsive: {
      0: {
        items: 2,
      },
      640: {
        items: 3,
      },
      768: {
        items: 4,
      },
      1024: {
        items: 4,
      },
      1280: {
        items: 6,
      },
      1536: {
        items: 6,
      },
    },
    navElement: () => (
      <CustomNav onNextClick={handleNextClick} onPrevClick={handlePrevClick} />
    ),
  };

  const options = {
    speed: 500,
    max: 30,
  };
  return (
    <div class="trending-container mb-6">
      <div className="flex">
        <div className="text-white text-xl md:text-2xl font-semibold mb-4 flex items-center">
          <div>
            <img src="/assets/icons/fire.svg" alt="" className="w-6 h-6 mr-2" />
          </div>
          <div>Trending</div>
        </div>

        <div>
          <CustomNav
            onNextClick={handleNextClick}
            onPrevClick={handlePrevClick}
          />
        </div>
      </div>
      <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 xl:grid-cols-5 gap-3">
        <Tilt options={options}>
          <div className="trending-content flex justify-center rounded-lg mb-3 cursor-pointer ">
            <div className="trending-card rounded-lg">
              <div>
                <div className="trending-img w-full">
                  <img
                    src="/assets/images/dbsuper.jpg"
                    alt=""
                    className="w-36 h-[220px] sm:w-[144px] sm:h-[212px] md:w-40 md:h-[236px] lg:w-44 lg:h-[264px] xl:w-[144px] xl:h-[216px] 2xl:w-40 2xl:h-[240px] object-cover rounded-lg"
                  />
                </div>
                <div className="trending-info w-32 mt-2">
                  <div className="title text-sm md:text-md">
                    Dragon Ball Super
                  </div>
                </div>
                <div className="trending-rating bottom-section flex items-center text-white mt-1">
                  <i class="bx bxs-star mr-2 text-xl text-orange-500"></i>
                  <div className="text-gray-300 text-sm">82%</div>
                </div>
              </div>
            </div>
          </div>
        </Tilt>

        <Tilt options={options}>
          <div className="trending-content flex justify-center rounded-lg mb-3 cursor-pointer ">
            <div className="trending-card rounded-lg">
              <div>
                <div className="trending-img w-full">
                  <img
                    src="/assets/images/bleach_s2.webp"
                    alt=""
                    className="w-36 h-[220px] sm:w-[144px] sm:h-[212px] md:w-40 md:h-[236px] lg:w-44 lg:h-[264px] xl:w-[144px] xl:h-[216px] 2xl:w-40 2xl:h-[240px] object-cover rounded-lg"
                  />
                </div>
                <div className="trending-info w-32 mt-2">
                  <div className="title text-sm md:text-md">
                    Bleach Saison 2
                  </div>
                </div>
                <div className="trending-rating bottom-section flex items-center text-white mt-1">
                  <i class="bx bxs-star mr-2 text-xl text-orange-500"></i>
                  <div className="text-gray-300 text-sm">92%</div>
                </div>
              </div>
            </div>
          </div>
        </Tilt>

        <Tilt options={options}>
          <div className="trending-content flex justify-center rounded-lg mb-3 cursor-pointer ">
            <div className="trending-card rounded-lg">
              <div>
                <div className="trending-img w-full">
                  <img
                    src="/assets/images/deathnote.webp"
                    alt=""
                    className="w-36 h-[220px] sm:w-[144px] sm:h-[212px] md:w-40 md:h-[236px] lg:w-44 lg:h-[264px] xl:w-[144px] xl:h-[216px] 2xl:w-40 2xl:h-[240px] object-cover rounded-lg"
                  />
