import React from "react";
import { useEffect, useRef } from "react";
import VanillaTilt from "vanilla-tilt";

import "./mostpopular.css";

function Tilt(props) {
  const { options, ...rest } = props;
  const tilt = useRef(null);

  useEffect(() => {
    VanillaTilt.init(tilt.current, options);
  }, [options]);

  return <div ref={tilt} {...rest} />;
}

const MostPopular = () => {
  const options = {
    speed: 500,
    max: 30,
  };
  return (
    <div className="mostpopular-container">
      <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 text-white">
        <div className="mostpopular-content mb-3">
        <Tilt options={options}>
          <div className="mostpopular-card flex">
            <div className="mostpopular-img flex items-center mr-3 ">
              <div className="rounded-md h-[90px] w-14">
                <img
                  src="/assets/images/tokyoghoul.jpg"
                  className="object-cover rounded-md h-full w-full"
                  alt=""
                />
              </div>
            </div>
            <div className="mostpopular-title font-semibold mb-1">
              <div class="mostpopular-title-content text-white mb-1">
                Tokyo Ghoul
              </div>
              <div className="flex items-center text-gray-300">
                <div className="mr-6">TV</div>
                <div className="dot"></div>
                <div>Ep 64/64</div>
                <div className="dot"></div>
                <div className="ml-6">23 min</div>
              </div>
            </div>
          </div>
          </Tilt>
        </div>
      </div>
    </div>
  );
};

export default MostPopular;
