import React from 'react';
import OwlCarousel from 'react-owl-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';

const CustomNav = ({ onNextClick, onPrevClick }) => {
  return (
    <div className="owl-nav">
      <button className="owl-prev" onClick={onPrevClick}>
        <i className="fa fa-chevron-left"></i>
      </button>
      <button className="owl-next" onClick={onNextClick}>
        <i className="fa fa-chevron-right"></i>
      </button>
    </div>
  );
};

const MyCarousel = () => {
  const options = {
    items: 5,
    nav: true,
    navElement: () => (
      <CustomNav
        onNextClick={() => console.log('Next clicked')}
        onPrevClick={() => console.log('Prev clicked')}
      />
    ),
    rewind: true,
    margin: 10,
    responsive: {
      0: {
        items: 1,
      },
      600: {
        items: 3,
      },
      1000: {
        items: 5,
      },
    },
  };

  return (
    <div className="container">
      <div className="header">
        <div className="title">My Carousel</div>
        <CustomNav
          onNextClick={() => console.log('Next clicked')}
          onPrevClick={() => console.log('Prev clicked')}
        />
      </div>
      <div className="content">
        <OwlCarousel {...options}>
          {[1, 2, 3, 4, 5, 6, 7, 8].map((item) => (
            <div className="item" key={item}>
              <h4>{item}</h4>
            </div>
          ))}
        </OwlCarousel>
      </div>
    </div>
  );
};

export default MyCarousel;
