import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Link } from "react-router-dom";

import "../login/login.css";

const Register = () => {
  // const [name, setName] = useState("");
  // const [address, setAddress] = useState("");
  // const [email, setEmail] = useState("");
  // const [password, setPassword] = useState("");

  // const [errorMessage, setErrorMessage] = useState("");
  // const navigate = useNavigate("");

  // const handleSubmit = async (e) => {
  //   e.preventDefault();

  //   try {
  //     const response = await axios.post("http://localhost:8000/api/users", {
  //       name: name,
  //       address: address,
  //       email: email,
  //       password: password,
  //     });

  //     console.log(response.data);
  //     navigate("/login");
  //   } catch (error) {
  //     setErrorMessage("Email existe déjà");
  //   }
  // };



  const [title, setTitle] = useState("");
const [description, setDescription] = useState("");
const [coverImage, setCoverImage] = useState("");
const [image, setImage] = useState("");
const [status, setStatus] = useState("");
const [episodeCount, setEpisodeCount] = useState(0);
const [airedFrom, setAiredFrom] = useState("");
const [airedTo, setAiredTo] = useState("");
const [studio, setStudio] = useState("");
const [director, setDirector] = useState("");
const [writer, setWriter] = useState("");
const [genre, setGenre] = useState("");
const [rating, setRating] = useState(0);
const [criticScore, setCriticScore] = useState(0);
const [userScore, setUserScore] = useState(0);
const [errorMessage, setErrorMessage] = useState("");
const navigate = useNavigate("");

const handleSubmit = async (e) => {
e.preventDefault();

try {
const response = await axios.post("http://localhost:8000/api/animes", {
title: title,
description: description,
cover_image: coverImage,
image: image,
status: status,
episode_count: episodeCount,
aired_from: airedFrom,
aired_to: airedTo,
studio: studio,
director: director,
writer: writer,
genre: genre,
rating: rating,
critic_score: criticScore,
user_score: userScore
});
console.log(response.data);
navigate("/");

} catch (error) {
  setErrorMessage("Une erreur s'est produite lors de l'ajout de l'anime.");
  }
  };

  return (
    <div className="wrapper">
      <div id="stars"></div>
      <div id="stars2"></div>
      <div id="stars3"></div>
      <section className="content">
        <div class="flex flex-col items-center justify-center px-6 py-8 mx-auto md:h-[calc(100vh-6px)] lg:py-0">
          <div class="w-[370px] md:w-[550px] bg-white rounded-lg shadow dark:border md:mt-0 xl:p-0 dark:bg-gray-800 dark:border-gray-700">
            <div class="w-full p-6 space-y-4 md:space-y-6 sm:p-8">
              <h1 class="text-xl font-bold leading-tight tracking-tight text-gray-900 md:text-2xl dark:text-white">
                Create your account
              </h1>
              <form class="space-y-4 md:space-y-6" onSubmit={handleSubmit}>
                <div className="grid grid-cols-1 sm:grid-cols-1 md:grid-cols-1 lg:grid-cols-2 xl:grid-cols-2 gap-4">
                  <div>
                    <label
                      for="name"
                      class="block mb-2 text-sm font-medium text-gray-900 dark:text-white"
                    >
                      Your name
                    </label>
                    <input
                      type="text"
                      name="name"
                      id="name"
                      class="bg-gray-50 border border-gray-300 text-gray-900 sm:text-sm rounded-lg focus:ring-primary-600 focus:border-primary-600 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                      placeholder=""
                      required=""
                      
                    />
                  </div>
                  <div>
                    <label
                      for="adress"
                      class="block mb-2 text-sm font-medium text-gray-900 dark:text-white"
                    >
                      Adress
                    </label>
                    <input
                      type="text"
                      name="adress"
                      id="adress"
                      placeholder="123 Rue xyz"
                      class="bg-gray-50 border border-gray-300 text-gray-900 sm:text-sm rounded-lg focus:ring-primary-600 focus:border-primary-600 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                      required=""
                      
                    />
                  </div>

                  <div>
                    <label
                      for="email"
                      class="block mb-2 text-sm font-medium text-gray-900 dark:text-white"
                    >
                      Your email
                    </label>
                    <input
                      type="email"
                      name="email"
                      id="email"
                      class="bg-gray-50 border border-gray-300 text-gray-900 sm:text-sm rounded-lg focus:ring-primary-600 focus:border-primary-600 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                      placeholder="name@company.com"
                      required=""
                      
                    />
                    <div class="text-center">
                      <p class="mt-2 text-sm text-red-600 dark:text-red-500">
                        <span class="font-medium">{errorMessage}</span>
                      </p>
                    </div>
                  </div>
                  <div>
                    <label
                      for="password"
                      class="block mb-2 text-sm font-medium text-gray-900 dark:text-white"
                    >
                      Password
                    </label>
                    <input
                      type="password"
                      name="password"
                      id="password"
                      placeholder="••••••••"
                      class="bg-gray-50 border border-gray-300 text-gray-900 sm:text-sm rounded-lg focus:ring-primary-600 focus:border-primary-600 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                      required=""
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                    />
                  </div>
                </div>
                <button
                  type="submit"
                  class="w-full text-white bg-blue-600 hover:bg-primary-700 focus:ring-4 focus:outline-none focus:ring-primary-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-primary-600 dark:hover:bg-primary-700 dark:focus:ring-primary-800"
                >
                  Sign up
                </button>
                <p class="text-sm font-light text-gray-500 dark:text-gray-400">
                  Already have an account?{" "}
                  <Link
                    className="font-medium text-primary-600 hover:underline dark:text-primary-500"
                    to="/login"
                  >
                    Sign in
                  </Link>
                </p>
              </form>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Register;
